mapper.update:
update sys_user
username = #{username, jdbcType=VARCHAR}, password = #{password, jdbcType=VARCHAR}, is_enabled = #{enabled, jdbcType=TINYINT}, is_account_non_expired = #{accountNonExpired, jdbcType=TINYINT}, is_account_non_locked = #{accountNonLocked, jdbcType=TINYINT}, is_credentials_non_expired = #{credentialsNonExpired, jdbcType=TINYINT}
id = #{id, jdbcType=BIGINT}

mapper.select:
SELECT 
FROM sys_user


        AND username = #{username, jdbcType=VARCHAR}


        AND is_enabled = #{enabled, jdbcType=TINYINT}


        AND is_account_non_expired = #{accountNonExpired, jdbcType=TINYINT}


        AND is_account_non_locked = #{accountNonLocked, jdbcType=TINYINT}


        iAND s_credentials_non_expired = #{credentialsNonExpired, jdbcType=TINYINT}



    LIMIT #{pageNum},#{pageSize}

SELECT COUNT(*)
FROM sys_user


        AND username = #{username, jdbcType=VARCHAR}


        AND is_enabled = #{enabled, jdbcType=TINYINT}


        AND is_account_non_expired = #{accountNonExpired, jdbcType=TINYINT}


        AND is_account_non_locked = #{accountNonLocked, jdbcType=TINYINT}


        AND is_credentials_non_expired = #{credentialsNonExpired, jdbcType=TINYINT}
